function fetch_object(idname)
{
    var my_obj = document.getElementById(idname);
        //alert("idname==="+idname);
    return my_obj;

}

function toggle_nav(obj)
{
//alert(obj);

    for (index = 1; index<13; index++  )
    {
       var nav_img = fetch_object("nav_" + index);

       var sub_nav = fetch_object("sub_nav_" + index);

   var sub_nav_index =  fetch_object("sub_nav_0");

    sub_nav_index.style.display = "none";
//alert(index +":===="+ sub_nav);

        if (obj == index)
        {
            nav_img.src = "images/nav_" + index + "_on.jpg";

           sub_nav.style.display = "block";
        }
        else
        {
          nav_img.src = "images/nav_" + index + "_off.jpg";
         sub_nav.style.display = "none";
        }
    }

}
